const Sidebar = ({ currentPage, currentRole, onNavigation }) => {
  const navigationItems = [
    { id: 'dashboard', label: '📊 Dashboard', roles: ['Manager', 'HR', 'Config'] },
    { id: 'excepciones', label: '⚠️ Excepciones', roles: ['Manager', 'HR', 'Config'] },
