                        <select class="select"><option>es mayor que</option><option>es igual a</option><option>no está en la lista</option></select>
                        <input type="text" class="input" placeholder="Valor" style="flex:0.5;">
                        <select class="select" style="flex:0.5;"><option>Horas</option><option>Minutos</option><option>Días</option></select>
