        <a href="#config" data-nav="config" class="hidden">⚙️ Configuración</a>
      </nav>
    </aside>

    <main class="content">

      <div class="page" id="page-dashboard">
        <div class="headline">
          <h1>Dashboard</h1>
          <button class="btn btn-ghost" data-action="toggle-customize-dash">🎨 Personalizar Panel</button>
        </div>
        <div class="kpis dashboard-grid" id="kpiGrid"></div>
      </div>

