<content>@tailwind base;
@tailwind components;
@tailwind utilities;

@import url('https://fonts.googleapis.com/css2?family=Bebas+Neue&family=Exo:wght@300;400;600;700&display=swap');

:root {
  --bl-blue: #1A77D2;
  --bl-gold: #B1A96E;
  --bl-dark: #0D2240;
  --bl-white: #FFFFFF;
  --bl-bg: #0b1c35;
  --bl-bg-2: #0F274B;
  --bl-muted: #8FA2BF;
  --ok: #35C759;
  --warn: #ffb020;
  --err: #d94b4b;
  --shadow: 0 10px 30px rgba(0,0,0,.25);
  --radius: 18px;
}

* {
  box-sizing: border-box;
}

html, body {
  height: 100%;
}

body {
  margin: 0;
  background: radial-gradient(1200px 600px at 80% -10%, rgba(26,119,210,.25), transparent 60%),
              radial-gradient(1000px 500px at -10% 20%, rgba(177,169,110,.18), transparent 60%),
              var(--bl-bg);
  color: var(--bl-white);
  font-family: 'Exo', system-ui, -apple-system, 'Segoe UI', Roboto, Arial, sans-serif;
}

/* TCC Branding Classes */
.tcc-brand-title {
  font-family: 'Bebas Neue', sans-serif;
  letter-spacing: 0.6px;
  color: var(--bl-gold);
}

.tcc-card {
  background: linear-gradient(180deg, rgba(15,39,75,.9), rgba(10,26,49,.9));
  border: 1px solid rgba(255,255,255,.06);
  border-radius: var(--radius);
  box-shadow: var(--shadow);
}

.tcc-btn {
  background: linear-gradient(180deg, #C8C199, #B1A96E);
  color: #0A1730;
  font-weight: 700;
  border: 0;
  cursor: pointer;
  padding: 8px 12px;
  border-radius: 12px;
  box-shadow: var(--shadow);
  transition: transform 0.06s ease;
  display: inline-flex;
  align-items: center;
  gap: 6px;
}

.tcc-btn:active {
  transform: translateY(1px);
}

.tcc-btn-ghost {
  background: rgba(255,255,255,.08);
  color: var(--bl-white);
  border: 1px solid rgba(255,255,255,.12);
}

.tcc-input {
  background: rgba(255,255,255,.06);
  border: 1px solid rgba(255,255,255,.1);
  color: var(--bl-white);
  padding: 8px 10px;
  border-radius: 12px;
  outline: none;
  width: 100%;
}

.tcc-label {
  font-size: 12px;
  color: var(--bl-gold);
  letter-spacing: 0.4px;
  margin-bottom: 6px;
  display: block;
}

.tcc-muted {
  color: var(--bl-muted);
  font-size: 13px;
}

/* KPI Specific Styles */
.kpi-card {
  position: relative;
  overflow: hidden;
  transition: all 0.3s ease;
  cursor: pointer;
}

.kpi-card:hover {
  transform: translateY(-2px);
  box-shadow: 0 15px 40px rgba(0,0,0,.35);
}

.kpi-value {
  font-family: 'Bebas Neue', sans-serif;
  font-size: 40px;
  line-height: 1;
  margin-top: 6px;
  background: linear-gradient(135deg, var(--bl-gold), #D4C68A);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
}

.kpi-trend {
  display: flex;
  align-items: center;
  gap: 4px;
  font-size: 12px;
  margin-top: 4px;
}

.kpi-trend.positive {
  color: var(--ok);
}

.kpi-trend.negative {
  color: var(--err);
}

/* Builder Styles */
.builder-panel {
  background: var(--bl-bg-2);
  border: 1px solid rgba(255,255,255,.12);
  border-radius: var(--radius);
  box-shadow: var(--shadow);
}

.drag-zone {
  border: 2px dashed rgba(177,169,110,.3);
  border-radius: var(--radius);
  transition: all 0.3s ease;
}

.drag-zone.active {
  border-color: var(--bl-gold);
  background: rgba(177,169,110,.1);
}

/* Animations */
@keyframes fadeInUp {
  from {
    opacity: 0;
    transform: translateY(20px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

.animate-fade-in-up {
  animation: fadeInUp 0.5s ease-out;
}

@keyframes pulse {
  0%, 100% {
    opacity: 1;
  }
  50% {
    opacity: 0.7;
  }
}

.animate-pulse-subtle {
  animation: pulse 2s cubic-bezier(0.4, 0, 0.6, 1) infinite;
}

/* Scrollbar Styling */
::-webkit-scrollbar {
  width: 5px;
  height: 5px;
}

::-webkit-scrollbar-track {
  background-color: transparent;
}

::-webkit-scrollbar-thumb {
  border-radius: 25px;
  transition: all 0.3s;
  background-color: rgba(106, 115, 125, 0.2);
}

::-webkit-scrollbar-thumb:hover {
  background-color: rgba(106, 115, 125, 0.27);
}

::-webkit-scrollbar-corner {
  display: none;
}

/* Modal Styles */
.modal-overlay {
  background: rgba(0,0,0,.55);
  backdrop-filter: blur(4px);
}

.modal-panel {
  background: var(--bl-bg-2);
  border: 1px solid rgba(255,255,255,.12);
  border-radius: 16px;
  box-shadow: var(--shadow);
}

/* Responsive Design */
@media (max-width: 768px) {
  .kpi-value {
    font-size: 32px;
  }

  .tcc-brand-title {
    font-size: 20px;
  }
}
</content>
